import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAlert } from 'react-alert';
import UserContext from '../../context/userContext';

const Register = () => {
  // const [user, setUser] = useState({
  //   name: '',
  //   email: '',
  //   password: '',
  //   password2: ''
  // });
  const { user, setUser } = useContext(UserContext);
  const { name, email, password, password2 } = user;
  const alert = useAlert();

  const onChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const clearFields = e => {
    setUser({
      name: '',
      email: '',
      password: '',
      password2: ''
    });
  };

  const success = e => {
    console.log('123');
  };

  const onSubmit = e => {
    e.preventDefault();
    console.log('123');
  };

  return (
    <form className='container' onSubmit={onSubmit}>
      <div className='form-row'>
        <div className='form-group col-md-4'>
          <label></label>
          <input
            type='text'
            className='form-control'
            name='name'
            placeholder='Name'
            value={name}
            onChange={onChange}
            required
          ></input>
        </div>
        <div className='form-group col-md-6'>
          <label></label>
          <input
            type='email'
            className='form-control'
            name='email'
            placeholder='Email'
            value={email}
            onChange={onChange}
            required
          ></input>
        </div>
        <div className='form-group col-md-6'>
          <label></label>
          <input
            type='password'
            className='form-control'
            name='password'
            placeholder='Password'
            value={password}
            onChange={onChange}
            required
            minLength='6'
          ></input>
        </div>
      </div>
      <div className='form-row'>
        <div className='form-group col-md-6'>
          <label></label>
          <input
            type='password'
            className='form-control'
            name='password2'
            placeholder='Confirm Password'
            value={password2}
            onChange={onChange}
            minLength='6'
            required
          ></input>
        </div>
      </div>

      {name &&
      email.includes('@') &&
      password === password2 &&
      password.length > 5 ? (
        <div>
          <Link to='/login'>
            <button className='btn btn-primary' onClick={success}>
              Register
            </button>
          </Link>
          <hr />
        </div>
      ) : (
        <div>
          <button
            type='submit'
            className='btn btn-primary'
            onClick={() => {
              alert.show('enter all fields...');
            }}
            type='submit'
          >
            Register
          </button>
          <hr />
        </div>
      )}
    </form>
  );
};

export default Register;
