import React, { useState, useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import UserContext from '../../context/userContext';
import AlertContext from '../../context/alertContext';

const Register = () => {
  // const [user, setUser] = useState({
  //   name: '',
  //   email: '',
  //   password: '',
  //   password2: ''
  // });
  const { user, setUser } = useContext(UserContext);
  const { name, email, password, password2 } = user;
  const alertContext = useContext(AlertContext);

  const { setAlert } = alertContext;

  const onChange = e => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const clearFields = e => {
    setUser({
      name: '',
      email: '',
      password: '',
      password2: ''
    });
  };

  const alert = e => {
    e.preventDefault();
    console.log('alert');
    if (name === '') {
      setAlert('enter name', 'danger');
    }
  };

  const success = e => {
    console.log('123');
  };

  const onSubmit = e => {
    e.preventDefault();
    console.log('123');
  };

  return (
    <form className='container' onSubmit={onSubmit}>
      <div className='form-row'>
        <div className='form-group col-md-4'>
          <label></label>
          <input
            type='text'
            className='form-control'
            name='name'
            placeholder='Name'
            value={name}
            onChange={onChange}
            required
          ></input>
        </div>
        <div className='form-group col-md-6'>
          <label></label>
          <input
            type='email'
            className='form-control'
            name='email'
            placeholder='Email'
            value={email}
            onChange={onChange}
            required
          ></input>
        </div>
        <div className='form-group col-md-6'>
          <label></label>
          <input
            type='password'
            className='form-control'
            name='password'
            placeholder='Password'
            value={password}
            onChange={onChange}
            required
            minLength='6'
          ></input>
        </div>
      </div>
      <div className='form-row'>
        <div className='form-group col-md-6'>
          <label></label>
          <input
            type='password'
            className='form-control'
            name='password2'
            placeholder='Confirm Password'
            value={password2}
            onChange={onChange}
            minLength='6'
            required
          ></input>
        </div>
      </div>

      {name &&
      email.includes('@') &&
      password === password2 &&
      password.length > 5 ? (
        <div>
          <Link to='/login'>
            <button onClick={success}>Register</button>
          </Link>
          <hr />
        </div>
      ) : (
        <div>
          <button
            // type='submit'
            // onClick={clearFields}
            className='btn btn-primary'
            type='button'
            data-toggle='collapse'
            data-target='#alert'
            aria-expanded='false'
            aria-controls='collapseExample'
          >
            Register
          </button>
          <hr />
          <div className='collapse' id='alert'>
            <div
              key={alert.id}
              className={`alert alert-danger`}
              id='alert'
              // style={{ display: 'none' }}
            >
              <i className='material-icons footer-icon-position'>error</i>
              fill out all fields...
            </div>
          </div>
        </div>
      )}
    </form>
  );
};

export default Register;
